import React, { useState } from "react";
import ReactCardFlip from "react-card-flip";
import "../index.css";
//import Button from 'react-bootstrap'
import "bootstrap/dist/css/bootstrap.min.css";
import "./card-style.css";

function Flipcard(props) {
  const [isFlipped, setIsFlipped] = useState(false);

  function handleClick() {
    setIsFlipped(!isFlipped);
  }
  function Options(props) {
    const isContent = props.isContent;
    if (isContent) {
      return (
        <div>
          <input type="radio" name="q1" value="3 types" /> {isContent}
          <br />
          <br />
        </div>
      );
    } else {
      return <div></div>;
    }
  }
  return (
    <ReactCardFlip
      isFlipped={isFlipped}
      flipDirection="horizontal"
      flipSpeedFrontToBack="1.2"
      flipSpeedBacktToFront="1.2"

    >
      <div>
        <div>
          <div>
            <h3 style={{ paddingTop: 20, textAlign: "left" }}>{props.name}</h3>
            <form
              name="self evaluation"
              id="self eval"
              style={{ paddingTop: 20, textAlign: "left" }}
            >
              <div class="radio">
                <Options isContent={props.content.option1} />
                <Options isContent={props.content.option2} />
                <Options isContent={props.content.option3} />
                <Options isContent={props.content.option4} />
              </div>
              <br />
            </form>
            <button
              onClick={handleClick}
              className="btn btn-success btn-lg float-left"
              style={{ paddingTop: 20, textAlign: "left" }}
            >
              <strong
                style={{ paddingTop: 20, textAlign: "left" }}
                className="text-center"
              >
                Submit
              </strong>
            </button>
          </div>
        </div>
        <div>
          <div style={{ height: 350, width: 800 }} className="question">
            <p
              style={{
                paddingTop: 20,
                justifyContent: "center",
                textAlign: "left",
              }}
            >
              There are three - <br />
              <br />
              Basic COCOMO Model, <br />
              <br />
              Intermediate COCOMO Model,
              <br />
              <br />
              Complete COCOMO Model{" "}
            </p>
            {/* <h3 style={{ paddingTop: 20, textAlign: "left" }}>
            This is the back of the card.
          </h3> */}
            <button
              onClick={handleClick}
              className="float-left btn btn-primary"
            >
              <strong>Go back to question</strong>
            </button>
          </div>
        </div>
      </div>
    </ReactCardFlip>
  );
}
export default Flipcard;
